import React, { useEffect, useState } from 'react'
import DefaultLayout from '../components/DefaultLayout'
import { useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { getAllCars } from '../redux/actions/carsActions'
import Spinner from '../components/Spinner'
import { Col, Row,Divider,TimeRangePickerProps, Select, Checkbox } from 'antd'
import { DatePicker, Space } from 'antd';
import moment from 'moment'
const { RangePicker } = DatePicker;
function BookinCar() {
const {carid}=useParams()
const {cars}=useSelector(state=>state.carsReducer)
const {loading}=useSelector(state=>state.alertsReducer)
const [car,setCar]=useState({})
const dispatch=useDispatch()
const [from,setFrom]=useState()
const [to,setTo]=useState()
const [totalHours,setTotalHours]=useState(0)
const [driver,setDriver]=useState(false)
const [TotalAmount,setTotalAmount]=useState(0)
useEffect(()=>{
  if(cars.length==0)
  {
    dispatch(getAllCars())
  }
else {
      setCar(cars.find(o=>o._id==carid))
}
},[cars])
function SelectTimeSlots(value){
setFrom(moment(value[0]).format("MMM DD yyyy HH:mm"))
setTo(moment(value[1]).format("MMM DD yyyy HH:mm"))
setTotalHours(value[1].diff(value[0],'hours'))
}
useEffect(()=>{
  setTotalHours((totalHours* parseInt(car.rentPerHour)) + driver && (totalHours*30))
},[driver,totalHours])
  return (
  <DefaultLayout>
    {loading && (<Spinner/>) }
    <Row justify="center" className='d-flex align-items-center' style={{minHeight:"90vh"}}>
      <Col lg={10} sm={24} xs={24}>
        <img src={car.image} className='carimg2 bs1'/>
      </Col>
      <Col lg={10} sm={24} xs={24}>
        <Divider type='horizontal' dashed>Car Info</Divider>
      <div style={{textAlign:"right"}}>
        <p>{car.name}</p>
        <p>{car.rentPerHour}</p>
        <p>fuel:{car.fuelType}</p>
        <p>Max Persons:{car.capacity}</p>
      </div>
      
        <Divider type='horizontal' dashed>Select Time Slots</Divider>
        <Space direction="vertical" size={12}>
          <RangePicker showTime={{format:'HH:mm'}} format='MMM DD yyyy HH:mm' onChange={SelectTimeSlots}/>
        
        </Space>
        <div>
          <p>Total Hours: {totalHours}</p>
          <p>Rent Per Hour : {car.rentPerHour}</p>
          <Checkbox onChange={(e)=>{
            if(e.target.checked){
              setDriver(true)
            }else{
              setDriver(false)
            }
          }}>driver Required</Checkbox>
          <h3>TotalAmount : {TotalAmount}</h3>
        </div>
      </Col>
    </Row>
   
  </DefaultLayout>
  )
}

export default BookinCar
