import React, { useEffect } from 'react'
import DefaultLayout from '../components/DefaultLayout'
import { useDispatch, useSelector } from 'react-redux'
import { getAllCars } from '../redux/actions/carsActions'
import { Col, Row,Divider, Select, Checkbox } from 'antd'
import Spinner from '../components/Spinner';
import { Link } from 'react-router-dom';
import {  Space,RangePicker } from 'antd';

function Home() {
  const { RangePicker } = DatePicker;

  const {cars}=useSelector(state=>state.carsReducer)
  const {loading}=useSelector(state=>state.alertsReducer)
  const dispatch=useDispatch()
useEffect(()=>{
dispatch(getAllCars())
},[])

  return (
    <DefaultLayout>
      <Row className='mt-3'>
        <Col lg={20} sm={24}>
        <Space direction="vertical" size={12}>
          <RangePicker showTime={{format:'HH:mm'}} format='MMM DD  HH:mm' />
        
        </Space>
        </Col>
      </Row>
      {loading == true && (<Spinner/>)}
    <Row justify='center' gutter={16} >
    {cars.map(car=>{
    return <Col lg={5} sm={24} xs={24}>
    <div style={{}} className="car p-2 bs1 ">
    <img src={car.image} className="carimg"/>
    <div className="car-content d-flex align-items-center justify-content-between">
    <div>
      <p style={{fontWeight:"bold"}}> {car.name}</p>
      <p> {car.rentPerHour}Rent Per Day /-</p>
    </div>
    <div>
      <button className="btn1 mr-2" ><Link to={`/booking/${car._id}`}>Book Now</Link></button>
    </div>
    </div>
    </div>
    </Col>
    })}
    </Row>
    </DefaultLayout>
  )
}

export default Home
